// farm_management_system/public/js/poultry_batches_listview.js
frappe.listview_settings['Poultry Batches'] = {
    refresh: function(list_view) {
        // Add both actions under one grouped menu "Actions"
        // Use the third parameter (group) to place them together
        // Make Collection
        list_view.page.add_inner_button(__('Make Collection'), () => {
            const dialog = new frappe.ui.Dialog({
                title: __('Make Collection'),
                fields: [
                    { fieldtype: 'Date', fieldname: 'date_of_collection', label: __('Date of Collection'), default: frappe.datetime.get_today(), reqd: 1 },
                    { fieldtype: 'Link', fieldname: 'animal', label: __('Specify Animal'), options: 'Animals', default: "Chicken", reqd: 1 },
                    {
                        fieldtype: 'Table',
                        fieldname: 'production_table',
                        label: __('Collections Table'),
                        fields: [
                            { fieldtype: 'Link', fieldname: 'poultry_batch', label: __('Poultry Batch'), options: 'Poultry Batches', in_list_view: 1 },
                            { fieldtype: 'Link', fieldname: 'animal_product', label: __('Animal Product'), options: 'Animal Products', reqd: 1, in_list_view: 1 },
                            { fieldtype: 'Data', fieldname: 'default_uom', label: __('Default UOM'), read_only: 1, in_list_view: 1 },
                            { fieldtype: 'Float', fieldname: 'quantity_collected', label: __('Quantity Collected'), reqd: 1, in_list_view: 1 }
                        ]
                    }
                ],
                primary_action_label: __('Make Entry'),
                primary_action: function() {
                    const dvalues = dialog.get_values(true);
                    const today = frappe.datetime.get_today();
                    const selected_date = dvalues && dvalues.date_of_collection;
                    if (selected_date && selected_date > today) {
                        frappe.show_alert({ message: __('Date of Collection cannot be a future date.'), indicator: 'red' }, 5);
                        return;
                    }

                    const rows = dvalues.production_table || [];
                    if (!rows.length) {
                        frappe.show_alert({ message: __('No products found to collect.'), indicator: 'orange' }, 5);
                        return;
                    }

                    frappe.warn(
                        __('Are you sure you want to proceed?'),
                        __('Please Note this Action is Irreversible'),
                        () => {
                            frappe.dom.freeze(__('Creating collection...'));
                            frappe.call({
                                method: 'farm_management_system.savanna_farm_suite.doctype.poultry_batches.poultry_batches.create_collection_entry',
                                args: {
                                    date_of_collection: dvalues.date_of_collection,
                                    rows: rows
                                },
                                callback: function(r) {
                                    frappe.dom.unfreeze();
                                    if (!r.exc && r.message) {
                                        frappe.utils.play_sound('success');
                                        dialog.hide();
                                        list_view.refresh();
                                        frappe.show_alert({ message: __('Collection recorded'), indicator: 'green' });
                                    } else if (r.exc) {
                                        frappe.show_alert({ message: __('Error: ') + (r.exc || __('Unknown error occurred')), indicator: 'red' }, 10);
                                    }
                                },
                                error: function() {
                                    frappe.dom.unfreeze();
                                    frappe.show_alert({ message: __('Network Error: Please try again.'), indicator: 'red' }, 10);
                                }
                            });
                        },
                        'Continue',
                        true
                    );
                }
            });

            dialog.show();
            dialog.$wrapper.find('.modal-dialog').addClass('modal-lg');

            // ---------- Helpers ----------
            function get_tbl() {
                return dialog.fields_dict && dialog.fields_dict.production_table;
            }

            // Ensure animal_product choices are filtered by product_tied_to_which_animal
            function set_product_query_for_animal(animal_name) {
                const tbl = get_tbl();
                if (!tbl || !tbl.grid) return;
                const grid = tbl.grid;

                const get_query_fn = function() {
                    if (!animal_name) return { filters: {} };
                    return { filters: { product_tied_to_which_animal: animal_name } };
                };

                // Top-level grid field (if supported)
                try {
                    if (typeof grid.get_field === 'function') {
                        const topField = grid.get_field('animal_product');
                        if (topField) {
                            topField.get_query = get_query_fn;
                            if (topField.df) topField.df.get_query = get_query_fn;
                        }
                    }
                } catch (e) { console.warn('set_product_query_for_animal: top-level set failed', e); }

                // Per-row fields (reliable)
                try {
                    (grid.grid_rows || []).forEach(function(gr) {
                        try {
                            const f = gr.fields_map && gr.fields_map.animal_product;
                            if (f) {
                                f.get_query = get_query_fn;
                                if (f.df) f.df.get_query = get_query_fn;

                                // Clear stale product if animal changed
                                if (gr.doc && gr.doc.animal_product) {
                                    gr.doc.animal_product = '';
                                    if (gr.refresh_field) gr.refresh_field('animal_product');
                                }
                            }
                        } catch (inner) { /* ignore per-row errors */ }
                    });
                } catch (e) { console.warn('set_product_query_for_animal: per-row set failed', e); }

                try { grid.refresh(); } catch (e) {}
            }

            // Set query for poultry_batch (only show Active)
            function set_batch_query() {
                const tbl = get_tbl();
                if (!tbl || !tbl.grid) return;
                const grid = tbl.grid;

                const get_query_fn = function() {
                    return { filters: { batch_status: "Active" } };
                };

                try {
                    if (typeof grid.get_field === 'function') {
                        const topField = grid.get_field('poultry_batch');
                        if (topField) {
                            topField.get_query = get_query_fn;
                            if (topField.df) topField.df.get_query = get_query_fn;
                        }
                    }
                } catch (e) { console.warn('set_batch_query top set failed', e); }

                try {
                    (grid.grid_rows || []).forEach(function(gr) {
                        try {
                            const f = gr.fields_map && gr.fields_map.poultry_batch;
                            if (f) {
                                f.get_query = get_query_fn;
                                if (f.df) f.df.get_query = get_query_fn;
                            }
                        } catch (inner) { /* ignore per-row errors */ }
                    });
                } catch (e) { console.warn('set_batch_query per-row set failed', e); }

                try { grid.refresh(); } catch (e) {}
            }

            function clear_production_table() {
                const tbl = get_tbl();
                if (!tbl) return;
                tbl.df.data = [];
                if (tbl.grid) tbl.grid.refresh();
            }

            // auto-add one row per active poultry batch (poultry_batches is array of {name})
            function populate_rows_for_batches(batches) {
                const tbl = get_tbl();
                if (!tbl || !tbl.grid) return;
                const grid = tbl.grid;

                tbl.df.data = [];
                grid.refresh();

                const rows = batches.map(b => ({
                    poultry_batch: b.name,
                    animal_product: '',
                    default_uom: '',
                    quantity_collected: 0.0
                }));

                tbl.df.data = rows;
                grid.refresh();

                // Lock poultry_batch per row and hide add/remove controls
                try {
                    (grid.grid_rows || []).forEach(function(gr) {
                        try {
                            if (gr.fields_map && gr.fields_map.poultry_batch) {
                                // mark read-only in df and set input readonly where available
                                gr.fields_map.poultry_batch.df && (gr.fields_map.poultry_batch.df.read_only = 1);
                                try { gr.fields_map.poultry_batch.$input && gr.fields_map.poultry_batch.$input.prop('readonly', true); } catch (e) {}
                            }

                            // hide remove button for this row
                            gr.toggle_remove_button && gr.toggle_remove_button(false);
                        } catch (inner) { /* ignore */ }
                    });

                    // hide global add-row controls so user cannot add rows
                    dialog.$wrapper.find('.grid-add-row, .grid-add-rows, .grid-row-add').hide();
                } catch (e) {
                    console.warn('populate_rows_for_batches: could not lock rows', e);
                }

                // Ensure product queries are set per-row now that rows exist
                set_product_query_for_animal(dialog.get_value('animal'));
                set_batch_query();
            }

            // Build rows for animal products (non-poultry)
            function populate_rows_for_products(products) {
                const tbl = get_tbl();
                if (!tbl || !tbl.grid) return;
                const grid = tbl.grid;

                tbl.df.data = [];
                grid.refresh();

                const rows = products.map(p => ({
                    poultry_batch: '',
                    animal_product: p.name,
                    default_uom: p.default_unit_of_measure || '',
                    quantity_collected: 0.0
                }));

                tbl.df.data = rows;
                grid.refresh();

                // In product-mode, allow add/remove rows
                try {
                    dialog.$wrapper.find('.grid-add-row, .grid-add-rows, .grid-row-add').show();
                    (grid.grid_rows || []).forEach(function(gr) {
                        try {
                            // ensure poultry_batch field is editable/blank
                            if (gr.fields_map && gr.fields_map.poultry_batch) {
                                gr.fields_map.poultry_batch.df && (gr.fields_map.poultry_batch.df.read_only = 0);
                                try { gr.fields_map.poultry_batch.$input && gr.fields_map.poultry_batch.$input.prop('readonly', false); } catch (e) {}
                                gr.toggle_remove_button && gr.toggle_remove_button(true);
                            }
                        } catch (inner) { /* ignore */ }
                    });
                } catch (e) {
                    console.warn('populate_rows_for_products: could not re-enable add/remove', e);
                }

                set_product_query_for_animal(dialog.get_value('animal'));
                set_batch_query();
            }

            // ---------- Events ----------

            // Initialize queries
            set_product_query_for_animal(null);
            set_batch_query();

            // When animal changes
            dialog.$wrapper.on('change', 'input[data-fieldname="animal"]', function() {
                const animal = $(this).val();
                set_product_query_for_animal(animal);
                set_batch_query();

                const is_poultry = ['Chicken', 'Poultry', 'Birds'].includes(animal);

                // Clear existing rows while fetching new ones
                clear_production_table();

                if (!animal) return;

                if (is_poultry) {
                    // fetch all active Poultry Batches and populate rows
                    frappe.call({
                        method: 'frappe.client.get_list',
                        args: {
                            doctype: 'Poultry Batches',
                            filters: { batch_status: 'Active' },
                            fields: ['name'],
                            limit_page_length: 2000
                        },
                        callback: function(r) {
                            if (!r.message || !r.message.length) {
                                clear_production_table();
                                frappe.show_alert({ message: __('No active Poultry Batches found'), indicator: 'orange' });
                                return;
                            }
                            populate_rows_for_batches(r.message);
                        },
                        error: function() {
                            frappe.show_alert({ message: __('Failed to fetch Poultry Batches'), indicator: 'red' });
                        }
                    });
                } else {
                    // non-poultry: fetch Animal Products for this animal and populate rows
                    frappe.call({
                        method: 'frappe.client.get_list',
                        args: {
                            doctype: 'Animal Products',
                            filters: { product_tied_to_which_animal: animal },
                            fields: ['name', 'default_unit_of_measure'],
                            limit_page_length: 1000
                        },
                        callback: function(r) {
                            if (!r.message || !r.message.length) {
                                clear_production_table();
                                frappe.show_alert({ message: __('No products found for selected animal'), indicator: 'orange' });
                                return;
                            }
                            populate_rows_for_products(r.message);
                        },
                        error: function() {
                            frappe.show_alert({ message: __('Failed to fetch Animal Products'), indicator: 'red' });
                        }
                    });
                }
            });

            // Auto-fill default_uom when product selected (awesomplete)
            dialog.$wrapper.on('awesomplete-selectcomplete', 'input[data-fieldname="animal_product"]', function() {
                const $input = $(this);
                const val = $input.val();
                const $row = $input.closest('.grid-row');
                const rowName = $row.attr('data-name');
                const tbl = get_tbl();
                if (!tbl || !tbl.grid) return;
                const grid = tbl.grid;

                frappe.call({
                    method: 'frappe.client.get_value',
                    args: { doctype: 'Animal Products', filters: { name: val }, fieldname: 'default_unit_of_measure' },
                    callback: function(r) {
                        const uom = (r && r.message && r.message.default_unit_of_measure) || '';
                        try {
                            if (grid.grid_rows && grid.grid_rows.length) {
                                const gr = grid.grid_rows.find(function(rr) { return rr && rr.wrapper && rr.wrapper.attr && rr.wrapper.attr('data-name') === rowName; }) || grid.grid_rows[0];
                                if (gr && gr.doc) {
                                    gr.doc.default_uom = uom;
                                    if (gr.refresh_field) gr.refresh_field('default_uom');
                                } else {
                                    grid.refresh();
                                }
                            } else {
                                grid.refresh();
                            }
                        } catch (e) { console.warn('set default_uom failed', e); }
                    }
                });
            });

            // When user adds a row manually later, ensure queries are applied to new row
            dialog.$wrapper.on('click', '.grid-add-row, .grid-add-rows, .grid-row-add', function() {
                setTimeout(function() {
                    const animal_val = dialog.get_value('animal');
                    set_product_query_for_animal(animal_val);
                    set_batch_query();
                }, 50);
            });

            // Prevent future date on blur
            dialog.$wrapper.on('blur', 'input[data-fieldname="date_of_collection"]', function() {
                const $input = $(this);
                const val = $input.val();
                if (!val) return;
                const today = frappe.datetime.get_today();
                if (val > today) {
                    frappe.show_alert({ message: __('Date of Collection cannot be a future date. The value has been reset to today.'), indicator: 'orange' }, 5);
                    dialog.set_value('date_of_collection', today);
                    $input.val(today);
                    try { $input.blur(); } catch (e) {}
                }
            });

            // initialize based on initial animal value
            const initial_animal = dialog.get_value('animal');
            if (['Chicken', 'Poultry', 'Birds'].includes(initial_animal)) {
                // populate poultry batches on load if default is poultry
                frappe.timeout(0).then(function() {
                    // small delay so dialog renders before we mutate grid
                    dialog.$wrapper.find('input[data-fieldname="animal"]').trigger('change');
                });
            } else if (initial_animal) {
                dialog.$wrapper.find('input[data-fieldname="animal"]').trigger('change');
            }

        }, __('Actions')).addClass('btn-primary');

        // Cull Animals (under same Actions group)
        list_view.page.add_inner_button(__('Cull Animals'), () => {
            // If one row selected in listview, pre-fill that batch
            const checked = list_view.get_checked_items();
            const default_batch = (Array.isArray(checked) && checked.length === 1) ? checked[0] : '';

            const cull_dialog = new frappe.ui.Dialog({
                title: __('Cull Animals'),
                fields: [
                    { fieldtype: 'Link', fieldname: 'poultry_batch', label: __('Specify Poultry Batch'), options: 'Poultry Batches', reqd: 1, default: default_batch },
                    { fieldtype: 'Int', fieldname: 'current_count', label: __('Current Poultry Count'), read_only: 1, description: __('Autopopulated')},
                    { fieldtype: 'Int', fieldname: 'count', label: __('Number of chickens to cull'), reqd: 1, description: __('Enter a positive whole number (no decimals)'), placeholder: __('e.g. 2, 30') }
                ],
                primary_action_label: __('Cull'),
                primary_action: function(values) {
                    const batch = values.poultry_batch;
                    const count = parseInt(values.count, 10);
                    const current = parseInt(values.current_count || 0, 10);

                    if (!batch) {
                        frappe.show_alert({ message: __('Please specify a Poultry Batch.'), indicator: 'red' }, 6);
                        return;
                    }
                    if (isNaN(count) || count <= 0) {
                        frappe.show_alert({ message: __('Please enter a positive whole number for the count.'), indicator: 'red' }, 6);
                        return;
                    }
                    if (isNaN(current) || current < 0) {
                        frappe.show_alert({ message: __('Current Poultry Count is unavailable. Please check the selected batch.'), indicator: 'red' }, 8);
                        return;
                    }
                    if (count > current) {
                        frappe.show_alert({ message: __('Cull count ({0}) exceeds current poultry count ({1}).', [count, current]), indicator: 'red' }, 8);
                        return;
                    }

                    cull_dialog.hide();

                    // irreversible confirmation
                    frappe.confirm(
                        __('This Action is irreversible! Do you want to continue?'),
                        () => {
                            // freeze UI
                            frappe.dom.freeze(__('Applying cull...'));

                            frappe.call({
                                method: 'farm_management_system.savanna_farm_suite.doctype.poultry_batches.poultry_batches.cull_poultry_batch',
                                args: {
                                    batch_name: batch,
                                    cull_count: count
                                },
                                callback: function(r) {
                                    frappe.dom.unfreeze();
                                    if (r.message && r.message.success) {
                                        frappe.utils.play_sound('success');
                                        frappe.show_alert({
                                            message: __(
                                                'Cull applied. Batch: {0}. New mortality_count: {1}, mortality_rate: {2}%',
                                                [batch, r.message.mortality_count, (r.message.mortality_rate || 0).toFixed(3)]
                                            ),
                                            indicator: 'green'
                                        }, 8);
                                        list_view.refresh();
                                    } else {
                                        const err = (r.message && r.message.error) || (r.exc ? r.exc : __('Unknown error'));
                                        frappe.show_alert({
                                            message: __('Error: {0}', [err]),
                                            indicator: 'red'
                                        }, 10);
                                    }
                                },
                                error: function() {
                                    frappe.dom.unfreeze();
                                    frappe.show_alert({
                                        message: __('Failed to call server'),
                                        indicator: 'red'
                                    }, 10);
                                }
                            });
                        },
                        () => {
                            // cancelled - no-op
                        }
                    );
                }
            });
            // helper: fetch total_animals & mortality_count and set current_count
            function fetchAndSetCurrentCount(batch_name) {
                if (!batch_name) {
                    cull_dialog.set_value('current_count', '');
                    return;
                }

                frappe.call({
                    method: 'frappe.client.get_value',
                    args: {
                        doctype: 'Poultry Batches',
                        filters: { name: batch_name },
                        fieldname: ['total_animals', 'mortality_count']
                    },
                    callback: function(r) {
                        if (!r || !r.message) {
                            cull_dialog.set_value('current_count', '');
                            frappe.show_alert({ message: __('Unable to fetch batch data.'), indicator: 'red' }, 6);
                            return;
                        }
                        const total = parseFloat(r.message.total_animals || 0);
                        const mort = parseFloat(r.message.mortality_count || 0);
                        let current = Math.max(0, Math.round(total - mort));
                        // if total/mort not set or invalid, show 0 and warn
                        if (isNaN(current)) current = 0;
                        cull_dialog.set_value('current_count', current);
                    },
                    error: function() {
                        cull_dialog.set_value('current_count', '');
                        frappe.show_alert({ message: __('Network error fetching batch data.'), indicator: 'red' }, 8);
                    }
                });
            }

            cull_dialog.show();
            cull_dialog.$wrapper.find('.modal-dialog').addClass('modal-md');
            if (default_batch) {
                fetchAndSetCurrentCount(default_batch);
            }

            // Listen to Link field changes (both typing/select and awesomplete event)
            cull_dialog.$wrapper.on('change', 'input[data-fieldname="poultry_batch"]', function() {
                const batch = $(this).val();
                fetchAndSetCurrentCount(batch);
            });
            cull_dialog.$wrapper.on('awesomplete-selectcomplete', 'input[data-fieldname="poultry_batch"]', function() {
                const batch = $(this).val();
                fetchAndSetCurrentCount(batch);
            });

        }, __('Actions')); // put under Actions group
    }
};

frappe.ui.form.on('Poultry Batches', {
    refresh(frm) {
        // This function is called when the form is refreshed.

        // Clear the existing chart container to prevent duplicates on reload
        frm.fields_dict.profitability_analysis_for_this_batch.$wrapper.html("");

        // Add containers for the charts
        frm.fields_dict.profitability_analysis_for_this_batch.$wrapper.html(`
            <div class="frappe-card p-4">
                <h4 class="mb-4">Weekly Feed Expense Analysis</h4>
                <div id="feed-expense-chart-container"></div>
            </div>
            <div class="frappe-card p-4 mt-4">
                <h4 class="mb-4">Weekly Animal Product Stock Value</h4>
                <div id="stock-value-chart-container"></div>
            </div>
        `);

        // Add a loading indicator
        const expenseChartContainer = document.getElementById('feed-expense-chart-container');
        const stockChartContainer = document.getElementById('stock-value-chart-container');
        expenseChartContainer.innerText = 'Loading Expense Data...';
        stockChartContainer.innerText = 'Loading Stock Value Data...';

        // Call the server-side function to get processed chart data
        frappe.call({
            method: "farm_management_system.savanna_farm_suite.doctype.poultry_batches.poultry_batches.get_profitability_chart_data",
            args: {
                batch_name: frm.doc.name
            },
            callback: function(response) {
                if (response.message) {
                    const expenseData = response.message.expense_data;
                    const stockData = response.message.stock_data;

                    // --- Render Expense Chart ---
                    if (expenseData && expenseData.labels && expenseData.labels.length > 0) {
                        const expenseChart = new frappe.Chart("#feed-expense-chart-container", {
                            title: "Weekly Feed Costs per Feed Type",
                            data: expenseData,
                            type: 'line',
                            height: 300,
                            colors: ['#743ee2', '#ff5858', '#00a8ff', '#ffc312', '#2bcbba'],
                            lineOptions: {
                                regionFill: 1
                            }
                        });
                    } else {
                        expenseChartContainer.innerHTML = '<p class="text-muted">No nourishment log data found for this batch to generate expense chart.</p>';
                    }

                    // --- Render Stock Value Chart ---
                    if (stockData && stockData.labels && stockData.labels.length > 0) {
                       const stockValueChart = new frappe.Chart("#stock-value-chart-container", {
                            title: "Weekly Stock Value per Animal Product",
                            data: stockData,
                            type: 'line',
                            height: 300,
                            colors: ['#ff6b6b', '#feca57', '#48dbfb', '#1dd1a1', '#5f27cd'],
                             lineOptions: {
                                regionFill: 0
                            }
                        });
                    } else {
                         stockChartContainer.innerHTML = '<p class="text-muted">No stock ledger entries found for this batch to generate stock value chart.</p>';
                    }

                } else {
                     expenseChartContainer.innerText = 'Failed to load chart data.';
                     stockChartContainer.innerText = 'Failed to load chart data.';
                }
            }
        });
    }
});

frappe.ui.form.on("Crop Intake", {
    refresh(frm) {

        // Render calendar if the document is saved
        if (frm.doc.name) {
            generate_schedule_calendar(frm);
        }
    },
});

function generate_schedule_calendar(frm) {
	const wrapper_field = frm.get_field('schedule_calendar');
	if (!wrapper_field) return;

	const $wrap = wrapper_field.$wrapper;
	$wrap.empty();

	const rows = (frm.doc.table_biyv || []).filter(r => r.scheduled_date);
	if (!rows.length) {
		$wrap.html('<div class="text-muted">No scheduled activities found to visualize.</div>');
		return;
	}

	// HTML escape + newline -> <br>
	const esc = (s) => {
		if (s === null || s === undefined) return '';
		return String(s)
			.replace(/&/g, "&amp;")
			.replace(/</g, "&lt;")
			.replace(/>/g, "&gt;")
			.replace(/"/g, "&quot;")
			.replace(/'/g, "&#039;")
			.replace(/\n/g, "<br>");
	};

	// group rows by YYYY-MM-DD
	const groupByDate = {};
	rows.forEach((r) => {
		if (!r.scheduled_date) return;
		let raw = r.scheduled_date;
		let key = String(raw).substr(0, 10); // Assumes YYYY-MM-DD format
		if (!groupByDate[key]) groupByDate[key] = [];
		groupByDate[key].push({
			activity: r.activity_being_undertaken || '',
			staff: r.staff_name || '',
			row: r
		});
	});

	// deterministic "random" color per date
	function hashString(s) {
		let h = 2166136261 >>> 0;
		for (let i = 0; i < s.length; i++) {
			h ^= s.charCodeAt(i);
			h += (h << 1) + (h << 4) + (h << 7) + (h << 8) + (h << 24);
		}
		return (h >>> 0);
	}
	function colorForKey(key) {
		const h = hashString(key) % 360;
		const light = 75 - (hashString(key + 'L') % 15);
		const sat = 65 + (hashString(key + 'S') % 20);
		return `hsl(${h}, ${sat}%, ${light}%)`;
	}

	// container id
	const id = `farm-schedule-calendar-${(Math.random()*1e9|0)}`;

	const calendarHTML = `
		<style>
		#${id} { font-family: system-ui, -apple-system, "Segoe UI", Roboto, "Helvetica Neue", Arial; max-width: 700px; }
		#${id} .cal-header { display:flex; align-items:center; justify-content:space-between; margin-bottom:8px; }
		#${id} .cal-nav { cursor:pointer; padding:6px 10px; border-radius:6px; user-select:none; background: #eee; }
        #${id} .cal-nav:hover { background: #ddd; }
		#${id} .cal-month { font-weight:600; font-size:1.05rem; }
		#${id} .cal-grid { display:grid; grid-template-columns:repeat(7,1fr); gap:6px; }
		#${id} .weekday { text-align:center; font-size:0.8rem; color:#666; padding:6px 0; }
		#${id} .day { min-height:70px; border-radius:8px; padding:6px; position:relative; background: #f7f7f7; outline: none; }
		#${id} .day.outside { opacity:0.25; background:transparent; }
		#${id} .day .date-num { font-weight:600; font-size:0.95rem; display:block; margin-bottom:6px; }
		#${id} .has-activities { cursor:pointer; box-shadow: inset 0 0 0 2px rgba(0,0,0,0.03); color: #fff; }
        #${id} .has-activities .date-num { color: #fff; text-shadow: 0 1px 2px rgba(0,0,0,0.2); }
		#${id} .cal-footer { margin-top:10px; color:#666; font-size:0.85rem; }
		</style>

		<div id="${id}">
			<div class="cal-header">
				<div>
					<span class="cal-nav" id="${id}-prev" title="Previous month">&#9664;</span>
					<span class="cal-nav" id="${id}-next" style="margin-left:8px;" title="Next month">&#9654;</span>
				</div>
				<div class="cal-month" id="${id}-month"></div>
				<div style="width:80px"></div>
			</div>
			<div class="cal-grid" id="${id}-weekdays">
				<div class="weekday">Sun</div><div class="weekday">Mon</div><div class="weekday">Tue</div><div class="weekday">Wed</div><div class="weekday">Thu</div><div class="weekday">Fri</div><div class="weekday">Sat</div>
			</div>
			<div class="cal-grid" id="${id}-days" style="margin-top:8px;"></div>
			<div class="cal-footer">Highlighted days have scheduled farm activities. Hover for details.</div>
		</div>
	`;

	$wrap.append(calendarHTML);

	let currentMonth, currentYear;
	const keys = Object.keys(groupByDate);
	if (keys.length) {
		const sorted = keys.slice().sort();
		const first = new Date(sorted[0] + 'T00:00:00');
		currentMonth = first.getMonth();
		currentYear = first.getFullYear();
	} else {
		const now = new Date();
		currentMonth = now.getMonth();
		currentYear = now.getFullYear();
	}

	const $monthLabel = $wrap.find(`#${id}-month`);
	const $days = $wrap.find(`#${id}-days`);
	$wrap.find(`#${id}-prev`).on('click', () => { currentMonth--; if (currentMonth < 0) { currentMonth = 11; currentYear--; } renderMonth(); });
	$wrap.find(`#${id}-next`).on('click', () => { currentMonth++; if (currentMonth > 11) { currentMonth = 0; currentYear++; } renderMonth(); });

	let tooltipEl = null;
	function createTooltip() {
		if (tooltipEl) return tooltipEl;
		tooltipEl = document.createElement('div');
		Object.assign(tooltipEl.style, {
			position: 'fixed', zIndex: 1061, padding: '10px 12px', borderRadius: '8px',
			background: 'rgba(255,255,255,0.98)',
			boxShadow: '0 10px 30px rgba(0,0,0,0.12)', border: '1px solid #ddd',
			maxWidth: '320px', fontSize: '0.87rem', color: '#111',
			display: 'none', pointerEvents: 'none'
		});
		document.body.appendChild(tooltipEl);
		return tooltipEl;
	}
	function showTooltipFor(dateKey, x, y, activities) {
		const el = createTooltip();
		const parts = activities.map(a => {
			return `<div style="margin-bottom:6px; padding-bottom:6px; border-bottom:1px solid #eee; font-size: 13px;">
                        <div><strong>Nature of Activity:</strong> ${esc(a.activity)}</div>
                        <div><strong>Assigned To:</strong> ${esc(a.staff)}</div>
                    </div>`;
		}).join('');
		el.innerHTML = parts.slice(0, -1) || '<div style="opacity:0.7">No details</div>'; // remove last border
		el.style.display = 'block';
		positionTooltip(x, y, el);
	}
	function hideTooltip() {
		if (tooltipEl) tooltipEl.style.display = 'none';
	}
	function positionTooltip(clientX, clientY, el) {
		if (!el) el = tooltipEl;
		const pad = 12;
		const rect = el.getBoundingClientRect();
		const vw = window.innerWidth;
		const vh = window.innerHeight;
		let left = clientX + 12;
		let top = clientY + 16;
		if (left + rect.width + pad > vw) left = clientX - rect.width - 12;
		if (top + rect.height + pad > vh) top = clientY - rect.height - 12;
		el.style.left = `${Math.max(pad, left)}px`;
		el.style.top = `${Math.max(pad, top)}px`;
	}

	function attachDayHandlers($container) {
		$container.off('.farmCal').on('mouseenter.farmCal', '.has-activities', function (ev) {
			const dateKey = $(this).data('date-key');
			if (!dateKey || !groupByDate[dateKey]) return;
			showTooltipFor(dateKey, ev.clientX, ev.clientY, groupByDate[dateKey]);
			const moveHandler = (e) => positionTooltip(e.clientX, e.clientY);
			$(document).on('mousemove.farmCal', moveHandler);
			$(this).data('farmCal_moveHandler', moveHandler);
		}).on('mouseleave.farmCal', '.has-activities', function () {
			const moveHandler = $(this).data('farmCal_moveHandler');
			if (moveHandler) $(document).off('mousemove.farmCal', moveHandler);
			hideTooltip();
		});
	}

	function pad(n) { return String(n).padStart(2,'0'); }
	function dateKeyFromYMD(y,m,d) { return `${y}-${pad(m)}-${pad(d)}`; }

	function renderMonth() {
		const monthNames = ["January","February","March","April","May","June","July","August","September","October","November","December"];
		$monthLabel.text(`${monthNames[currentMonth]} ${currentYear}`);
		$days.empty();

		const firstWeekday = new Date(currentYear, currentMonth, 1).getDay();
		const daysInMonth = new Date(currentYear, currentMonth + 1, 0).getDate();

		for (let i = 0; i < firstWeekday; i++) $days.append(`<div class="day outside"></div>`);

		for (let d = 1; d <= daysInMonth; d++) {
			const key = dateKeyFromYMD(currentYear, currentMonth + 1, d);
			const activities = groupByDate[key] || [];
			const has = !!activities.length;
			const $day = $(`<div class="day ${has ? 'has-activities' : ''}" data-date-key="${key}"></div>`);
			$day.append(`<span class="date-num">${d}</span>`);
			if (has) {
				$day.css('background', colorForKey(key));
				const preview = esc((activities[0] && activities[0].activity) || '');
				$day.append(`<div style="font-size:0.78rem;opacity:0.9;max-height:40px;overflow:hidden">${preview}</div>`);
			}
			$days.append($day);
		}

		const totalCells = firstWeekday + daysInMonth;
		const trailing = (7 - (totalCells % 7)) % 7;
		for (let i = 0; i < trailing; i++) $days.append(`<div class="day outside"></div>`);

		attachDayHandlers($days);
	}

	renderMonth();

    // Re-render calendar if child table data changes
	try {
		const tableWrapper = frm.get_field('table_biyv').$wrapper.get(0);
		if (tableWrapper && !tableWrapper._farm_calendar_observer) {
			const mo = new MutationObserver(() => {
				if (tableWrapper._farm_calendar_timer) clearTimeout(tableWrapper._farm_calendar_timer);
				tableWrapper._farm_calendar_timer = setTimeout(() => {
					generate_schedule_calendar(frm);
				}, 250);
			});
			mo.observe(tableWrapper, { childList: true, subtree: true });
			tableWrapper._farm_calendar_observer = mo;
		}
	} catch (e) {
		console.error('Farm calendar observer error', e);
	}
}

