// Function to load scheduled dates from table_biyv - FIXED VERSION
function loadScheduledDates(frm, callback) {
    // Clear the cache first
    window.scheduledDatesCache = {};

    // Fetch scheduled dates from the table_biyv using a direct database query
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'Farm Activity Schedule',
            filters: {
                activity_tied_to_which_crop_batch: frm.doc.name
            },
            fields: ['name']
        },
        callback: function(r) {
            if (r.message && r.message.length > 0) {
                // Get all schedule documents
                const scheduleNames = r.message.map(item => item.name);

                // Use a server-side method to get all child table data at once
                frappe.call({
                    method: 'farm_management_system.savanna_farm_suite.doctype.crop_intake.crop_intake.get_scheduled_activities',
                    args: {
                        schedule_names: scheduleNames
                    },
                    callback: function(res) {
                        if (res.message) {
                            res.message.forEach((row, index) => {
                                if (row.scheduled_date) {
                                    window.scheduledDatesCache[row.scheduled_date] = {
                                        index: (index % 6) + 1,
                                        activity: row.activity_being_undertaken || 'Scheduled Activity',
                                        description: row.status || '',
                                        assignees: row.staff_name || ''
                                    };
                                }
                            });
                        }
                        callback();
                    }
                });
            } else {
                callback();
            }
        }
    });
}

// Function to generate activity calendar
function generateActivityCalendar(dialog, month, year, frm) {
    let firstDay = new Date(year, month, 1);
    let lastDay = new Date(year, month + 1, 0);
    let daysInMonth = lastDay.getDate();
    let startingDay = firstDay.getDay();

    const monthNames = ["January", "February", "March", "April", "May", "June",
        "July", "August", "September", "October", "November", "December"];

    // Create calendar HTML with legend
    let calendarHTML = `
        <div class="activity-calendar-header">
            <h3>${monthNames[month]} ${year}</h3>
        </div>
        <div class="legend-container">
            <strong>Click on any set date in the calendar to record Farming Activity.</strong><br>
        </div>
        <div class="activity-calendar">
            <div class="activity-calendar-weekdays">
                <div>Sun</div><div>Mon</div><div>Tue</div><div>Wed</div><div>Thu</div><div>Fri</div><div>Sat</div>
            </div>
            <div class="activity-calendar-days">
    `;

    // Add empty cells for days before the first day of the month
    for (let i = 0; i < startingDay; i++) {
        calendarHTML += `<div class="activity-calendar-day empty"></div>`;
    }

    // Add cells for each day of the month
    for (let i = 1; i <= daysInMonth; i++) {
        const dateStr = `${year}-${String(month + 1).padStart(2, '0')}-${String(i).padStart(2, '0')}`;
        const scheduledData = window.scheduledDatesCache[dateStr];
        const scheduledClass = scheduledData ? ` scheduled-${scheduledData.index}` : '';
        const title = scheduledData ? `title="${scheduledData.activity} - ${scheduledData.assignees}"` : '';

        calendarHTML += `
            <div class="activity-calendar-day${scheduledClass}" data-date="${dateStr}" ${title}>
                <div style="font-size: 16px; margin-bottom: 5px;">${i}</div>
                ${scheduledData ? `<div class="scheduled-activity-indicator">${scheduledData.activity.substring(0, 10)}...</div>` : ''}
            </div>
        `;
    }

    calendarHTML += `</div></div>`;

    // Set calendar HTML
    dialog.fields_dict.calendar_container.$wrapper.html(calendarHTML);

    // Add click event to each day
    dialog.$wrapper.find('.activity-calendar-day:not(.empty)').on('click', function() {
        const date = $(this).data('date');
        openActivityRecordDialog(date, frm, dialog);
    });
}

// Function to add navigation to activity calendar
function addActivityCalendarNavigation(dialog, month, year, frm) {
    const navHTML = `
        <div class="activity-calendar-navigation">
            <button class="btn btn-default prev-month">
                <i class="fa fa-chevron-left"></i> Previous Month
            </button>
            <button class="btn btn-default today-btn">
                Today
            </button>
            <button class="btn btn-default next-month">
                Next Month <i class="fa fa-chevron-right"></i>
            </button>
        </div>
    `;

    dialog.$wrapper.find('.activity-calendar-header').after(navHTML);

    // Previous month handler
    dialog.$wrapper.find('.prev-month').on('click', function() {
        let newMonth = month - 1;
        let newYear = year;
        if (newMonth < 0) {
            newMonth = 11;
            newYear = year - 1;
        }
        generateActivityCalendar(dialog, newMonth, newYear, frm);
        addActivityCalendarNavigation(dialog, newMonth, newYear, frm);
    });

    // Next month handler
    dialog.$wrapper.find('.next-month').on('click', function() {
        let newMonth = month + 1;
        let newYear = year;
        if (newMonth > 11) {
            newMonth = 0;
            newYear = year + 1;
        }
        generateActivityCalendar(dialog, newMonth, newYear, frm);
        addActivityCalendarNavigation(dialog, newMonth, newYear, frm);
    });

    // Today button handler
    dialog.$wrapper.find('.today-btn').on('click', function() {
        let today = new Date();
        generateActivityCalendar(dialog, today.getMonth(), today.getFullYear(), frm);
        addActivityCalendarNavigation(dialog, today.getMonth(), today.getFullYear(), frm);
    });
}

