                            frappe.msgprint(__('Error creating Farm Operation Log: {0}', [r.message]));
                        }
                    });
                }
            );
        }
    });

    // Fetch options for dropdowns
    let employeeList = [];
    let activityList = [];
    let inputList = [];

    const empPromise = new Promise((resolve, reject) => {
        frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: 'Employee',
                fields: ['name', 'employee_name'],
                limit: 0,
                ignore_permissions: true
            },
            callback: function(r) {
                employeeList = r.message || [];
                resolve();
            },
            error: reject
        });
    });

    const actPromise = new Promise((resolve, reject) => {
        frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: 'Crop Activity',
                fields: ['name'],
                limit: 0,
                ignore_permissions: true
            },
            callback: function(r) {
                activityList = r.message || [];
                resolve();
            },
            error: reject
        });
    });

    const inputPromise = new Promise((resolve, reject) => {
        frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: 'Farm Inputs',
                fields: ['name', 'uom'],
                limit: 0,
                ignore_permissions: true
            },
            callback: function(r) {
                inputList = r.message || [];
                resolve();
            },
            error: reject
        });
    });

    Promise.all([empPromise, actPromise, inputPromise]).then(() => {
        // Setup labourers table
        activityDialog.fields_dict.labourers_html.$wrapper.html(`
            <div class="labourers-container">
                <button class="btn btn-sm btn-default add-labourer-btn">
                    <i class="fa fa-plus"></i> Add Labourer
                </button>
                <div class="labourers-list" style="margin-top: 10px;"></div>
            </div>
        `);

        activityDialog.$wrapper.find('.add-labourer-btn').on('click', function() {
            addLabourerRow(activityDialog, labourersData, employeeList);
        });

        // Setup activities table
        activityDialog.fields_dict.activities_html.$wrapper.html(`
            <div class="activities-container">
                <button class="btn btn-sm btn-default add-activity-btn">
                    <i class="fa fa-plus"></i> Add Activity
                </button>
                <div class="activities-list" style="margin-top: 10px;"></div>
            </div>
        `);

        activityDialog.$wrapper.find('.add-activity-btn').on('click', function() {
            addActivityRow(activityDialog, activitiesData, activityList);
        });

        // Setup farm inputs table
        activityDialog.fields_dict.farm_inputs_html.$wrapper.html(`
            <div class="farm-inputs-container">
                <button class="btn btn-sm btn-default add-input-btn">
                    <i class="fa fa-plus"></i> Add Farm Input
                </button>
                <div class="farm-inputs-list" style="margin-top: 10px;"></div>
