// Function to add farm input row with dropdown + Current Stock column
function addFarmInputRow(dialog, farmInputsData, inputList) {
    const rowId = frappe.utils.get_random(5);
    const rowData = {
        id: rowId,
        farm_input: '',
        uom: '',
        quantity: 0,
        current_stock: 0
    };

    farmInputsData.push(rowData);

    const rowHTML = `
        <div class="farm-input-row" data-row-id="${rowId}" style="border: 1px solid #d1d8dd; padding: 10px; margin-bottom: 10px; border-radius: 4px;">
            <div class="row">
                <div class="col-md-3">
                    <div class="form-group">
                        <label>Farm Input Used</label>
                        <select class="form-control farm-input-select" data-fieldname="farm_input">
                            <option value="" disabled selected>Select Farm Input</option>
                            ${inputList.map(d => `<option value="${d.name}" data-uom="${d.uom || ''}">${d.name}</option>`).join('')}
                        </select>
                    </div>
                </div>
                <div class="col-md-2">
                    <div class="form-group">
                        <label>Default UOM</label>
                        <input type="text" class="form-control uom-field" data-fieldname="uom" readonly>
                    </div>
                </div>
                <div class="col-md-2">
                    <div class="form-group">
                        <label>Quantity Used</label>
                        <input type="number" class="form-control quantity-field" data-fieldname="quantity" step="0.01">
                    </div>
                </div>
                <div class="col-md-3">
                    <div class="form-group">
                        <label>Current Stock</label>
                        <input type="text" class="form-control current-stock-field" data-fieldname="current_stock" readonly>
                        <small class="stock-warning text-danger" style="display:none;">Requested qty > Current stock</small>
                    </div>
                </div>
                <div class="col-md-2">
                    <button class="btn btn-sm btn-danger remove-row-btn" style="margin-top: 25px;">
                        <i class="fa fa-times"></i>
                    </button>
                </div>
            </div>
        </div>
    `;

    dialog.$wrapper.find('.farm-inputs-list').append(rowHTML);

    // Setup farm input select
    const $row = dialog.$wrapper.find(`[data-row-id="${rowId}"]`);

    $row.find('.farm-input-select').on('change', function() {
        const select = this;
        const val = select.value;
        const opt = select.options[select.selectedIndex];
        const uom = opt ? opt.dataset.uom : '';
        $row.find('.uom-field').val(uom);
        const row = farmInputsData.find(r => r.id === rowId);
        if (row) {
            row.farm_input = val;
            row.uom = uom;
            row.current_stock = 0; // reset while fetching
            $row.find('.current-stock-field').val('');
            $row.removeClass('insufficient-stock-row');
            $row.find('.stock-warning').hide();
        }

        // Call server to get current stock for selected input
        if (val) {
            frappe.call({
                method: 'farm_management_system.savanna_farm_suite.doctype.crop_seedlings.crop_seedlings.get_current_stock',
                args: { item_code: val },
                freeze: false,
                callback: function(res) {
                    if (res && res.message) {
                        const stock_info = res.message;
                        const qty_after = parseFloat(stock_info.qty_after_transaction) || 0;
                        // set UI and data
                        $row.find('.current-stock-field').val(qty_after);
                        const row = farmInputsData.find(r => r.id === rowId);
                        if (row) {
                            row.current_stock = qty_after;
                            // If quantity already entered and exceeds stock, show inline warning
                            const enteredQty = parseFloat($row.find('.quantity-field').val()) || 0;
                            if (enteredQty > qty_after) {
                                $row.addClass('insufficient-stock-row');
                                $row.find('.stock-warning').show();
                            } else {
                                $row.removeClass('insufficient-stock-row');
                                $row.find('.stock-warning').hide();
                            }
                        }
                    } else {
                        // No message or failure  leave as blank/0
                        $row.find('.current-stock-field').val('');
                    }
                },
                error: function() {
                    // failure  ignore, but leave empty
                    $row.find('.current-stock-field').val('');
                }
            });
        }
    });

    // Handle quantity change
    $row.find('.quantity-field').on('input change', function() {
        const row = farmInputsData.find(r => r.id === rowId);
        const entered = parseFloat($(this).val()) || 0;
        if (row) {
            row.quantity = entered;
            // check against current_stock (if available)
            const avail = parseFloat(row.current_stock) || 0;
            if (avail && entered > avail) {
                $row.addClass('insufficient-stock-row');
                $row.find('.stock-warning').show();
            } else {
                $row.removeClass('insufficient-stock-row');
                $row.find('.stock-warning').hide();
            }
        }
    });

    // Remove row handler
    $row.find('.remove-row-btn').on('click', function() {
        $row.remove();
        const index = farmInputsData.findIndex(r => r.id === rowId);
        if (index > -1) {
            farmInputsData.splice(index, 1);
        }
    });
}

frappe.ui.form.on('Crop Intake', {
    refresh(frm) {
        // Wrap chart generation in a timeout to prevent rendering errors
        // This gives the DOM time to draw the container for the chart
        setTimeout(() => {
            const wrapper = frm.get_field("farm_input_usage_trend").$wrapper;
            wrapper.empty();

            const source_data = frm.doc.table_voqq;

            if (!source_data || source_data.length === 0) {
                wrapper.html(`<div class="text-muted text-center" style="padding: 2rem;">
                    No farm input usage data to display.
                </div>`);
                return;
            }

            // 1. Process and Aggregate Data
            let data_by_date = {};
            let uom_lookup = {};
            let all_agents = new Set();

            source_data.forEach(d => {
                if (!d.date_of_use || !d.farming_agent_used || !d.quantity_of_farming_agent_used) {
                    return;
                }

                if (!data_by_date[d.date_of_use]) {
                    data_by_date[d.date_of_use] = {};
                    uom_lookup[d.date_of_use] = {};
                }

                const current_qty = data_by_date[d.date_of_use][d.farming_agent_used] || 0;
                data_by_date[d.date_of_use][d.farming_agent_used] = current_qty + d.quantity_of_farming_agent_used;

                // Important: This relies on 'd.agents_uom' having a value
                uom_lookup[d.date_of_use][d.farming_agent_used] = d.agents_uom;

                all_agents.add(d.farming_agent_used);
            });

            // **DEBUGGING STEP**: Check your browser's console (F12) for this output
            // It will show you if the UOMs are being captured correctly.
            console.log("UOM Lookup Table:", uom_lookup);

            // 2. Prepare Data for Frappe Charts
            const sorted_agents = Array.from(all_agents).sort();
            const labels = Object.keys(data_by_date).sort((a, b) => new Date(a) - new Date(b));

            const datasets = sorted_agents.map(agent => {
                return {
                    name: agent,
                    values: labels.map(date => data_by_date[date][agent] || 0)
                };
            });

            // 3. Render the Chart
            new frappe.Chart(wrapper[0], {
                title: "Farm Input Usage Trend",
                data: {
                    labels: labels,
                    datasets: datasets
                },
                type: 'bar',
                height: 300,
                colors: ['#743EE2', '#2E93fA', '#66DA26', '#E91E63', '#FF9800'],
                barOptions: {
                    stacked: 1
                },
                tooltipOptions: {
                    format: (points) => {
                        if (!points || points.length === 0) return;

                        const point = points[0];
                        const date_label = frappe.datetime.str_to_user(point.label);

                        let tooltip_html = `<div class="graph-tooltip-title">${date_label}</div>
                                            <ul class="tooltip-list">`;

                        points.filter(p => p.value > 0).forEach(p => {
                            const agent = p.dataset_name;
                            const quantity = p.value;

                            // More robust lookup for the UOM
                            const uom = (uom_lookup[p.label] && uom_lookup[p.label][agent]) ? uom_lookup[p.label][agent] : '';

                            tooltip_html += `<li>
                                <div class="flex justify-between">
                                    <div>
                                        <span class="tooltip-legend-color" style="background-color: ${p.color}"></span>
                                        <span class="tooltip-label">${agent}</span>
                                    </div>
                                    <span class="tooltip-value">${quantity} ${uom}</span>
                                </div>
                            </li>`;
                        });

                        tooltip_html += `</ul>`;
                        return tooltip_html;
                    }
                }
            });
        }, 300); // 300ms delay to ensure the container is ready
    }
});

