/**
 * Handler for the Cull button click  now with batch link + current_count autopopulate.
 */
function handleCullClick(frm) {
    const label = __('Cull Animals'); // keep same label as refresh() uses

    // Locate the header button (safe-guard)
    const $btn = frm.page.wrapper.find(`button[data-label="${label}"]`);
    if ($btn.data('processing')) return;

    const d = new frappe.ui.Dialog({
        title: label,
        fields: [
            {
                fieldtype: 'Link',
                fieldname: 'poultry_batch',
                label: __('Specify Poultry Batch'),
                options: 'Poultry Batches',
                reqd: 1,
                default: frm.doc.name || ''
            },
            {
                fieldtype: 'Int',
                fieldname: 'current_count',
                label: __('Current Poultry Count'),
                read_only: 1,
                description: __('Autopopulated: total_animals - mortality_count')
            },
            {
                fieldtype: 'Int',
                fieldname: 'count',
                label: __('Number of chickens to cull'),
                reqd: 1,
                description: __('Enter a positive whole number (no decimals)'),
                placeholder: __('e.g. 2, 30')
            }
        ],
        primary_action_label: __('Cull'),
        primary_action(values) {
            const batch = values.poultry_batch;
            const count = parseInt(values.count, 10);
            const current = parseInt(values.current_count || 0, 10);

            // final validations
            if (!batch) {
                frappe.show_alert({ message: __('Please specify a Poultry Batch.'), indicator: 'red' }, 6);
                return;
            }
            if (isNaN(count) || count <= 0) {
                frappe.show_alert({ message: __('Please enter a positive whole number for the count.'), indicator: 'red' }, 6);
                return;
            }
            if (isNaN(current) || current < 0) {
                frappe.show_alert({ message: __('Current Poultry Count is unavailable. Please check the selected batch.'), indicator: 'red' }, 6);
                return;
            }
            if (count > current) {
                frappe.show_alert({ message: __('Cull count ({0}) exceeds current poultry count ({1}).', [count, current]), indicator: 'red' }, 8);
                return;
            }

            d.hide();

            // confirmation
            frappe.confirm(
                __('This Action is irreversible! Do you want to continue?'),
                () => {
                    // mark header button processing (prevent re-clicks)
                    $btn.data('processing', true).prop('disabled', true);

                    frappe.dom.freeze(__('Applying cull...'));
                    frappe.call({
                        method: 'farm_management_system.savanna_farm_suite.doctype.poultry_batches.poultry_batches.cull_poultry_batch',
                        args: {
                            batch_name: batch,
                            cull_count: count
                        },
                        callback: function (r) {
                            frappe.dom.unfreeze();
                            $btn.data('processing', false).prop('disabled', false);

                            if (r.message && r.message.success) {
                                frappe.utils.play_sound('success');
                                frappe.show_alert({
                                    message: __(
                                        'Cull applied. New mortality_count: {0}, mortality_rate: {1}%',
                                        [r.message.mortality_count, (r.message.mortality_rate || 0).toFixed(3)]
                                    ),
                                    indicator: 'green'
                                }, 7);
                                frm.reload_doc();
                            } else {
                                const err = (r.message && r.message.error) || (r.exc ? r.exc : __('Unknown error'));
                                frappe.show_alert({ message: __('Error: {0}', [err]), indicator: 'red' }, 10);
                            }
                        },
                        error: function () {
                            frappe.dom.unfreeze();
                            $btn.data('processing', false).prop('disabled', false);
                            frappe.show_alert({ message: __('Failed to call server'), indicator: 'red' }, 10);
                        }
                    });
                },
                () => {
                    // cancelled
                }
            );
        }
    });

    // show dialog
    d.show();
    d.$wrapper.find('.modal-dialog').addClass('modal-md');

    // get primary action button DOM so we can enable/disable it
    const $primary_btn = d.$wrapper.find('.modal-footer .btn-primary');
    // start disabled until a valid batch with current_count > 0 exists
    if ($primary_btn.length) $primary_btn.prop('disabled', true);

    // helper to fetch totals and set current_count, also enable/disable primary button
    function fetchAndSetCurrentCount(batch_name) {
        d.set_value('current_count', ''); // clear while fetching

        if (!batch_name) {
            d.set_value('current_count', '');
            if ($primary_btn.length) $primary_btn.prop('disabled', true);
            return;
        }

        frappe.call({
            method: 'frappe.client.get_value',
            args: {
                doctype: 'Poultry Batches',
                filters: { name: batch_name },
                fieldname: ['total_animals', 'mortality_count']
            },
            callback: function (r) {
                if (!r || !r.message) {
                    d.set_value('current_count', '');
                    frappe.show_alert({ message: __('Unable to fetch batch data.'), indicator: 'red' }, 6);
                    if ($primary_btn.length) $primary_btn.prop('disabled', true);
                    return;
                }

                const total = parseFloat(r.message.total_animals || 0);
                const mort = parseFloat(r.message.mortality_count || 0);
                let current = Math.round(total - mort);
                if (!isFinite(current) || current < 0) current = 0;

                d.set_value('current_count', current);

                // enable primary only if current > 0
                if ($primary_btn.length) $primary_btn.prop('disabled', !(current > 0));
            },
            error: function () {
                d.set_value('current_count', '');
                frappe.show_alert({ message: __('Network error fetching batch data.'), indicator: 'red' }, 8);
                if ($primary_btn.length) $primary_btn.prop('disabled', true);
            }
        });
    }

    // If the dialog default included frm.doc.name, fetch now
    const initial_batch = frm.doc.name || '';
    if (initial_batch) {
        fetchAndSetCurrentCount(initial_batch);
    }

    // Listen to Link field changes (both typing/select and awesomplete event)
    d.$wrapper.on('change', 'input[data-fieldname="poultry_batch"]', function() {
        const batch = $(this).val();
        fetchAndSetCurrentCount(batch);
    });
    d.$wrapper.on('awesomplete-selectcomplete', 'input[data-fieldname="poultry_batch"]', function() {
        const batch = $(this).val();
        fetchAndSetCurrentCount(batch);
    });
}

frappe.ui.form.on('Crop Intake', {
	refresh(frm) {
		frm.add_custom_button(__('Record Harvest'), function() {
                if (frm.is_new()) {
                    frappe.msgprint(__('Please save the document before recording harvest.'));
                    return;
                }

                openHarvestDialog(frm);

            }, __('Action')).addClass('btn-primary');

		if (frm.is_new()) {
			return;
		}

		const wrapper = frm.fields_dict.harvest_trend.wrapper;
		wrapper.innerHTML = `
			<div id="harvest-chart" style="height: 300px;"></div>
			<div id="stock-info" style="color: red; font-weight: bold; margin-top: 20px;"></div>
		`;

		const harvests = frm.doc.table_yuhl || [];
		if (!harvests.length) {
			$(wrapper).find('#harvest-chart').html('<p>No harvest data available.</p>');
			return;
		}

		const data_map = {};
		const product_uom = {};
		harvests.forEach(h => {
			const date = h.date_of_collection;
			const prod = h.product_collected;
			if (!data_map[date]) data_map[date] = {};
			if (!data_map[date][prod]) data_map[date][prod] = 0;
			data_map[date][prod] += h.quantity_collected;
			if (!product_uom[prod]) product_uom[prod] = h.products_default_uom;
		});

		const labels = Object.keys(data_map).sort();
		const products = [...new Set(harvests.map(h => h.product_collected))];

		const datasets = products.map(prod => ({
			name: prod,
			values: labels.map(date => data_map[date][prod] || 0),
			chartType: 'bar'
		}));

		const colors = ['#7cd6fd', '#743ee2', '#ff5858', '#ffa00a', '#0acf97', '#fa5c7c'];

		new frappe.Chart('#harvest-chart', {
			data: {
				labels: labels,
				datasets: datasets
			},
			type: 'bar',
			height: 300,
			colors: products.map((p, i) => colors[i % colors.length]),
			axisOptions: {
				xAxisMode: 'tick',
				xIsSeries: false
			},
			barOptions: {
				stacked: false,
				spaceRatio: 0.5
			},
			tooltipOptions: {
				formatTooltipX: d => d,
				formatTooltipY: (value, datasetName) => {
					const uom = product_uom[datasetName] || '';
					return value + ' ' + uom;
				}
			}
		});

		const promises = products.map(prod => {
			return new Promise(resolve => {
				frappe.call({
					method: 'farm_management_system.savanna_farm_suite.doctype.crop_seedlings.crop_seedlings.get_current_stock',
					args: { item_code: prod },
					callback: r => {
						resolve({ product: prod, data: r.message || {} });
					}
				});
			});
		});

		Promise.all(promises).then(results => {
			let html = '';
			results.forEach(res => {
				const d = res.data;
				html += `
					${res.product}<br>
					Current Stock: ${d.qty_after_transaction || 'N/A'}<br>
					Stock Value: ${d.stock_value || 'N/A'}<br>
					Current Valuation Rate: ${d.valuation_rate || 'N/A'}<br><br>
				`;
			});
			$(wrapper).find('#stock-info').html(html);
		});
	}
});

function openHarvestDialog(frm) {
    const dialog = new frappe.ui.Dialog({
        title: __('Record Harvest'),
        fields: [
            { 
                fieldtype: 'Date', 
                fieldname: 'harvesting_date', 
                label: __('Harvesting Date'), 
                default: frappe.datetime.get_today(), 
                reqd: 1 
            },
            { 
                fieldtype: 'Link', 
                fieldname: 'crop', 
                label: __('Crop being Harvested'), 
                options: 'Crop', 
                reqd: 1 
            },
            { 
                fieldtype: 'Link', 
                fieldname: 'crop_batch', 
                label: __('Specify Crop Batch'), 
                options: 'Crop Intake', 
                reqd: 1,
                default: frm.doc.name,
                read_only: 1
            },
            {
                fieldtype: 'Table',
                fieldname: 'harvest_table',
                label: __('Harvest Details'),
                fields: [
                    { 
                        fieldtype: 'Link', 
                        fieldname: 'crop_product', 
                        label: __('Crop Product'), 
                        options: 'Crop Products', 
                        reqd: 1, 
                        in_list_view: 1 
                    },
                    { 
                        fieldtype: 'Link', 
                        fieldname: 'default_uom', 
                        label: __('Default UOM'), 
                        options: 'UOM', 
                        read_only: 1, 
                        in_list_view: 1 
                    },
                    { 
                        fieldtype: 'Float', 
                        fieldname: 'quantity_harvested', 
                        label: __('Quantity Harvested'), 
                        reqd: 1, 
                        in_list_view: 1 
                    }
                ]
            }
        ],
        primary_action_label: __('Record Harvest'),
        primary_action: function() {
            const values = dialog.get_values(true);

            // Validate future date
            const today = frappe.datetime.get_today();
            if (values.harvesting_date > today) {
                frappe.msgprint({
                    title: __('Invalid Date'),
                    message: __('Harvesting Date cannot be a future date.'),
                    indicator: 'red'
                });
                return;
            }

            // Validate rows
            if (!values.harvest_table || values.harvest_table.length === 0) {
                frappe.msgprint(__('Please add at least one product to the harvest table.'));
                return;
            }

            frappe.warn(
                __('Are you sure you want to record this harvest?'),
                __('This action will update multiple documents and create stock entries. This cannot be undone.'),
                () => {
                    frappe.dom.freeze(__('Recording harvest...'));
                    frappe.call({
                        method: 'farm_management_system.savanna_farm_suite.doctype.crop_intake.crop_intake.record_harvest_entry',
                        args: {
                            crop_batch: values.crop_batch,
                            harvesting_date: values.harvesting_date,
                            crop: values.crop,
                            rows: values.harvest_table
                        },
                        callback: function(r) {
                            frappe.dom.unfreeze();
                            if (!r.exc && r.message) {
                                frappe.utils.play_sound('success');
                                dialog.hide();
                                frm.refresh();
                                frappe.show_alert({ 
                                    message: __('Harvest recorded successfully'), 
                                    indicator: 'green' 
                                });
                            } else if (r.exc) {
                                frappe.msgprint({ 
                                    title: __('Error'), 
                                    message: r.exc, 
                                    indicator: 'red' 
                                });
                            }
                        },
                        error: function() {
                            frappe.dom.unfreeze();
                            frappe.msgprint({ 
                                title: __('Network Error'), 
                                message: __('Please try again.'), 
                                indicator: 'red' 
                            });
                        }
                    });
                },
                'Continue',
                true
            );
        }
    });

    dialog.show();
    dialog.$wrapper.find('.modal-dialog').addClass('modal-lg');

    // When crop is selected, fetch and populate crop products
    dialog.$wrapper.on('change', 'input[data-fieldname="crop"]', function() {
        const crop = $(this).val();
        if (!crop) {
            clearHarvestTable(dialog);
            return;
        }

        frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: 'Crop Products',
                filters: { crop: crop },
                fields: ['name', 'default_uom'],
                limit_page_length: 100
            },
            callback: function(r) {
                if (r.message && r.message.length > 0) {
                    populateHarvestTable(dialog, r.message);
                } else {
                    clearHarvestTable(dialog);
                    frappe.show_alert({ 
                        message: __('No crop products found for the selected crop'), 
                        indicator: 'orange' 
                    });
                }
            }
        });
    });

    // Auto-fill default UOM when crop product is selected
    dialog.$wrapper.on('awesomplete-selectcomplete', 'input[data-fieldname="crop_product"]', function() {
        const $input = $(this);
        const cropProduct = $input.val();
        const $row = $input.closest('.grid-row');
        const rowName = $row.attr('data-name');
        const grid = dialog.fields_dict.harvest_table.grid;

        if (!grid || !cropProduct) return;

        frappe.call({
            method: 'frappe.client.get_value',
            args: { 
                doctype: 'Crop Products', 
                filters: { name: cropProduct }, 
                fieldname: 'default_uom' 
            },
            callback: function(r) {
                const uom = (r && r.message && r.message.default_uom) || '';
                const row = grid.get_row(rowName);
                if (row && row.doc) {
                    row.doc.default_uom = uom;
                    if (row.refresh_field) row.refresh_field('default_uom');
                }
            }
        });
    });

    // Prevent future date on blur
    dialog.$wrapper.on('blur', 'input[data-fieldname="harvesting_date"]', function() {
        const $input = $(this);
        const val = $input.val();
        if (!val) return;
        const today = frappe.datetime.get_today();

        if (val > today) {
            frappe.msgprint({
                title: __('Invalid Date'),
                message: __('Harvesting Date cannot be a future date. The value has been reset to today.'),
                indicator: 'orange'
            });
            dialog.set_value('harvesting_date', today);
            $input.val(today);
        }
    });
}

function clearHarvestTable(dialog) {
    const tableField = dialog.fields_dict.harvest_table;
    if (tableField && tableField.grid) {
        tableField.grid.remove_all();
    }
}

function populateHarvestTable(dialog, cropProducts) {
    const tableField = dialog.fields_dict.harvest_table;
    if (!tableField || !tableField.grid) return;

    // Clear existing rows
    tableField.grid.remove_all();

    // Add rows for each crop product
    cropProducts.forEach(product => {
        const row = tableField.grid.add_new_row();
        row.doc.crop_product = product.name;
        row.doc.default_uom = product.default_uom;
        row.doc.quantity_harvested = 0.0;

        // Refresh fields to show values
        if (row.refresh_field) {
            row.refresh_field('crop_product');
            row.refresh_field('default_uom');
            row.refresh_field('quantity_harvested');
        }
    });

    tableField.grid.refresh();
}

